var captcha = {

    'default': {

        'id': 'recaptcha',
        'key': '123456789',

    },

    'init': function(options) {

        var parentObj = this,
            newOptions = siteOptions.setOptions({
                defaults: parentObj.default,
                source: isExist(options) ? options : {}
            });

        if($(document).find('#'+options.id).length <= 0) {
            return false;
        }
        grecaptcha.render(options.id, {'sitekey':options.key});

    },

};