//= ../modules/hyphen/jquery.hyphen.js
//= ../modules/hyphen/jquery.hyphen.ru.js
//= ../modules/hyphen/en-us.js

var hyphen = {

    'default': {
        'selector': '.hyphen',
    },

    'init': function(options) {

        var parentObj = this,
            newoptions = siteOptions.setOptions({
                defaults: parentObj.default,
                source: isExist(options) ? options : {}
            });

        $(options.selector)
            .hyphenate('en-us')
            .hyphenates();

    }

};