var loaderAwesome = {



    'defaults': {
        'container': 'test',
        'type': 'battery',
        'loader': 'loaderAwesome',
    },


    'layout': function(type) {
        var layout = ''+
                '<div class="loaderAwesome">'+
                    '<div class="loaderAwesome-' + type + '"></div>'+
                '</div>'+
                '';
        return layout;
    },


    'destroy': function(options) {

        var parentObj = this;
        $(document).find(options.container).find('.' + parentObj.defaults.loader).remove();

    },


    'init': function(options) {

        var parentObj = this,
            options = siteOptions.setOptions({
            defaults: parentObj.defaults,
            source: isExist(options) ? options : {}
        });
        $(document).find(options.container).append(parentObj.layout(options.type));

    },



};
